<script>
// Обработка формы подписки на рассылку
document.addEventListener('DOMContentLoaded', function() {
    const newsletterForm = document.getElementById('newsletter-form');
    const messageDiv = document.getElementById('newsletter-message');
    const submitBtn = document.getElementById('newsletter-submit');

    if (newsletterForm && submitBtn) {
        // Сохранить исходную структуру кнопки
        const originalButtonHTML = submitBtn.innerHTML;

        // Найти контейнер виджета для поиска сообщений
        const widgetContainer = newsletterForm.closest('.elementor-widget-container');

        // Скрыть сообщения об ошибках от Elementor
        const hideElementorMessages = function() {
            // Ищем сообщения внутри формы и контейнера виджета
            const containers = [newsletterForm];
            if (widgetContainer) {
                containers.push(widgetContainer);
            }
            containers.forEach(function(container) {
                const elementorMessages = container.querySelectorAll('.elementor-message');
                elementorMessages.forEach(function(msg) {
                    msg.style.display = 'none';
                });
            });
        };
        // Скрыть сообщения сразу и после небольшой задержки (на случай если они появляются динамически)
        hideElementorMessages();
        setTimeout(hideElementorMessages, 100);

        // Наблюдатель за изменениями DOM для скрытия сообщений Elementor
        const observer = new MutationObserver(function(mutations) {
            hideElementorMessages();
        });

        if (widgetContainer) {
            observer.observe(widgetContainer, {
                childList: true,
                subtree: true
            });
        }

        newsletterForm.addEventListener('submit', function(e) {
            e.preventDefault();

            // Скрыть сообщения Elementor
            hideElementorMessages();
            setTimeout(hideElementorMessages, 50);
            setTimeout(hideElementorMessages, 200);

            const emailInput = document.getElementById('form-field-email');
            const email = emailInput.value.trim();

            // Валидация на клиенте
            if (!email) {
                messageDiv.style.display = 'block';
                messageDiv.style.backgroundColor = '#fee';
                messageDiv.style.color = '#c33';
                messageDiv.style.border = '1px solid #c33';
                messageDiv.textContent = 'Пожалуйста, введите email адрес';
                return;
            }

            // Отключить кнопку и изменить текст
            submitBtn.disabled = true;
            const buttonText = submitBtn.querySelector('.elementor-button-text');
            if (buttonText) {
                buttonText.textContent = 'Отправка...';
            } else {
                submitBtn.innerHTML = '<span><span class="elementor-button-text">Отправка...</span></span>';
            }

            // Скрыть предыдущие сообщения
            messageDiv.style.display = 'none';

            // Создать FormData
            const formData = new FormData(newsletterForm);

            // AJAX запрос
            fetch('includes/newsletter-handler.php', {
                method: 'POST',
                body: formData
            })
            .then(response => response.json())
            .then(data => {
                if (data.success) {
                    messageDiv.style.display = 'block';
                    messageDiv.style.backgroundColor = '#efe';
                    messageDiv.style.color = '#3c3';
                    messageDiv.style.border = '1px solid #3c3';
                    messageDiv.textContent = data.message;
                    newsletterForm.reset();

                    // Скрыть сообщение через 5 секунд
                    setTimeout(() => {
                        messageDiv.style.display = 'none';
                    }, 5000);
                } else {
                    messageDiv.style.display = 'block';
                    messageDiv.style.backgroundColor = '#fee';
                    messageDiv.style.color = '#c33';
                    messageDiv.style.border = '1px solid #c33';
                    messageDiv.textContent = data.message || 'Произошла ошибка. Пожалуйста, попробуйте позже.';
                }
            })
            .catch(error => {
                messageDiv.style.display = 'block';
                messageDiv.style.backgroundColor = '#fee';
                messageDiv.style.color = '#c33';
                messageDiv.style.border = '1px solid #c33';
                messageDiv.textContent = 'Ошибка подключения. Пожалуйста, попробуйте позже.';
            })
            .finally(() => {
                submitBtn.disabled = false;
                // Восстановить исходную структуру кнопки
                submitBtn.innerHTML = originalButtonHTML;
            });
        });

        // Также скрывать сообщения Elementor при изменении формы
        newsletterForm.addEventListener('input', hideElementorMessages);
    }

    // Обработка формы "Свяжитесь с нами"
    const contactForm = document.getElementById('contact-form');

    if (contactForm) {
        // Создать элемент для отображения сообщений
        let messageDiv = document.createElement('div');
        messageDiv.id = 'contact-form-message';
        messageDiv.style.cssText = 'display: none; padding: 15px; margin: 15px 0; border-radius: 5px; text-align: center; font-weight: 500;';
        contactForm.insertBefore(messageDiv, contactForm.firstChild);

        // Найти контейнер виджета
        const widgetContainer = contactForm.closest('.elementor-widget-container');
        const submitBtn = contactForm.querySelector('button[type="submit"]');

        // Сохранить исходную структуру кнопки
        let originalButtonHTML = '';
        if (submitBtn) {
            originalButtonHTML = submitBtn.innerHTML;
        }

        // Скрыть сообщения от Elementor
        const hideElementorMessages = function() {
            if (widgetContainer) {
                const elementorMessages = widgetContainer.querySelectorAll('.elementor-message');
                elementorMessages.forEach(function(msg) {
                    msg.style.display = 'none';
                });
            }
        };

        contactForm.addEventListener('submit', function(e) {
            e.preventDefault();

            hideElementorMessages();
            setTimeout(hideElementorMessages, 50);
            setTimeout(hideElementorMessages, 200);

            // Валидация на клиенте
            const name = contactForm.querySelector('[name="form_fields[name]"]').value.trim();
            const email = contactForm.querySelector('[name="form_fields[email]"]').value.trim();
            const message = contactForm.querySelector('[name="form_fields[message]"]').value.trim();
            const acceptance = contactForm.querySelector('[name="form_fields[acceptance]"]').checked;

            if (!name || !email || !message || !acceptance) {
                messageDiv.style.display = 'block';
                messageDiv.style.backgroundColor = '#fee';
                messageDiv.style.color = '#c33';
                messageDiv.style.border = '1px solid #c33';
                messageDiv.textContent = 'Пожалуйста, заполните все поля и примите условия обработки данных';
                return;
            }

            // Отключить кнопку и изменить текст
            if (submitBtn) {
                submitBtn.disabled = true;
                const buttonText = submitBtn.querySelector('.elementor-button-text');
                if (buttonText) {
                    buttonText.textContent = 'Отправка...';
                } else {
                    // Если структура другая, сохраняем текущее состояние перед изменением
                    const currentHTML = submitBtn.innerHTML;
                    submitBtn.innerHTML = '<span><span class=" elementor-button-icon"></span><span class="elementor-button-text">Отправка...</span></span>';
                    // Сохраняем для восстановления, если не была сохранена раньше
                    if (!originalButtonHTML) {
                        originalButtonHTML = currentHTML;
                    }
                }
            }

            // Скрыть предыдущие сообщения
            messageDiv.style.display = 'none';

            // Создать FormData
            const formData = new FormData(contactForm);

            // AJAX запрос
            fetch('includes/contact-form-handler.php', {
                method: 'POST',
                body: formData
            })
            .then(response => response.json())
            .then(data => {
                if (data.success) {
                    messageDiv.style.display = 'block';
                    messageDiv.style.backgroundColor = '#efe';
                    messageDiv.style.color = '#3c3';
                    messageDiv.style.border = '1px solid #3c3';
                    messageDiv.textContent = data.message;
                    contactForm.reset();

                    // Скрыть сообщение через 5 секунд
                    setTimeout(() => {
                        messageDiv.style.display = 'none';
                    }, 5000);
                } else {
                    messageDiv.style.display = 'block';
                    messageDiv.style.backgroundColor = '#fee';
                    messageDiv.style.color = '#c33';
                    messageDiv.style.border = '1px solid #c33';
                    messageDiv.textContent = data.message || 'Произошла ошибка. Пожалуйста, попробуйте позже.';
                }
            })
            .catch(error => {
                messageDiv.style.display = 'block';
                messageDiv.style.backgroundColor = '#fee';
                messageDiv.style.color = '#c33';
                messageDiv.style.border = '1px solid #c33';
                messageDiv.textContent = 'Ошибка подключения. Пожалуйста, позвоните нам по телефону.';
            })
            .finally(() => {
                if (submitBtn) {
                    submitBtn.disabled = false;
                    // Восстановить исходную структуру кнопки
                    if (originalButtonHTML) {
                        submitBtn.innerHTML = originalButtonHTML;
                    } else {
                        const buttonText = submitBtn.querySelector('.elementor-button-text');
                        if (buttonText) {
                            buttonText.textContent = 'Отправить';
                        }
                    }
                }
            });
        });

        // Скрывать сообщения Elementor при изменении формы
        contactForm.addEventListener('input', hideElementorMessages);
    }

    // Инициализация Яндекс карты
    function initYandexMap() {
        if (typeof ymaps === 'undefined') {
            setTimeout(initYandexMap, 100);
            return;
        }

        ymaps.ready(function () {
            var mapContainer = document.getElementById('yandex-map');
            if (!mapContainer) return;

            var myMap = new ymaps.Map('yandex-map', {
                center: [58.602737, 49.660663], // Координаты ул. Садаковская, 2, Киров
                zoom: 17
            });

            // Поиск адреса и установка маркера
            ymaps.geocode('Киров, ул. Садаковская, 2').then(function (res) {
                var firstGeoObject = res.geoObjects.get(0);
                var coords = firstGeoObject.geometry.getCoordinates();

                // Обновляем центр карты на найденные координаты
                myMap.setCenter(coords, 17);

                // Добавляем маркер
                var myPlacemark = new ymaps.Placemark(coords, {
                    balloonContent: 'ул. Садаковская, 2, Киров<br>ООО БлагоСервис',
                    hintContent: 'ул. Садаковская, 2, Киров'
                }, {
                    preset: 'islands#blueDotIcon'
                });

                myMap.geoObjects.add(myPlacemark);
            });
        });
    }

    // Загрузка API Яндекс карт
    var script = document.createElement('script');
    script.src = 'https://api-maps.yandex.ru/2.1/?lang=ru_RU';
    script.async = true;
    script.onload = initYandexMap;
    document.head.appendChild(script);
});
</script>

